mapper.select:
select ltrim(sys_connect_by_path(area_name,'--'),'--')
from base_area ba where ba.base_area_id = #{baseAreaId}
start with  ba.parent_id is null connect by (prior ba.base_area_id)=ba.parent_id

select ba.base_area_id baseAreaId, ba.base_file_server_id baseFileServerId, ba.base_pms_server_id basePmsServerId, ba.area_name areaName, ba.area_code areaCode, ba.sort sort, ba.parent_id parentId, ba.edit_schedule editSchedule, ba.software_upgrade softwareUpgrade, ba.create_school createSchool, ba.area_level areaLevel, ba.area_path areaPath, ba.area_id_path areaIdPath, (select count(*) from base_area nba where nba.parent_id=ba.base_area_id) hasChilder, (select bcal.level_title from base_config_area_level bcal where bcal.area_level=ba.area_level) levelName from base_area ba where 1 = 1
and ba.parent_id is null
and ba.parent_id = #{parentId}

select cs.cls_school_id id, cs.base_area_id parentId, cs.school_name name from cls_school cs where 1=1 and cs.delete_flag != 'Y'
and cs.base_area_id is null
and cs.base_area_id = #{areaId}

select base_area_id baseAreaId, base_file_server_id baseFileServerId, base_pms_server_id basePmsServerId, area_name areaName,area_code areaCode, sort sort, parent_id parentId, edit_schedule editSchedule, software_upgrade softwareUpgrade, create_school createSchool, area_level areaLevel, map_source mapSource, map_output mapOutput from base_area where 1 = 1
and parent_id is null
and parent_id = #{parentId}
